/// Returns true if there is space in `self` for more bytes.
///
/// This is equivalent to `self.remaining_mut() != 0`.
///
/// # Examples
///
/// ```
/// use bytes::BufMut;
///
/// let mut dst = [0; 5];
/// let mut buf = &mut dst[..];
///
/// assert!(buf.has_remaining_mut());
///
/// buf.put(&b"hello"[..]);
///
/// assert!(!buf.has_remaining_mut());
/// ```
