/// Get the URI for this request
///
/// By default this is `/`.
///
/// # Examples
///
/// ```
/// # use http::*;
///
/// let mut req = Request::builder();
/// assert_eq!(req.uri_ref().unwrap(), "/" );
///
/// req = req.uri("https://www.rust-lang.org/");
/// assert_eq!(req.uri_ref().unwrap(), "https://www.rust-lang.org/" );
/// ```
