            &self.alloc,
            additional,
            &|table, index| hasher(table.bucket::<T>(index).as_ref()),
            fallibility,
            Self::TABLE_LAYOUT,
            if T::NEEDS_DROP {
                Some(|ptr| ptr::drop_in_place(ptr as *mut T))
            } else {
                None
            },
        )
    }
}
