///
/// [`is_finite`]: f64::is_finite
/// [`is_nan`]: f64::is_nan
/// [`is_infinite`]: f64::is_infinite
pub fn format_finite<F: Float>(&mut self, f: F) -> &str {
    unsafe {
        let n = f.write_to_ryu_buffer(self.bytes.as_mut_ptr() as *mut u8);
        debug_assert!(n <= self.bytes.len());
