        .split('/')
        .skip(1)
        .map(|x| x.replace("~1", "/").replace("~0", "~"))
        .try_fold(self, |target, token| match target {
            Value::Object(map) => map.get_mut(&token),
            Value::Array(list) => parse_index(&token).and_then(move |x| list.get_mut(x)),
            _ => None,
        })
}
