fn get_mut(&mut self, key: u32) -> &mut ValueType {
    if self.map.is_none() {
        self.allocate();
    }

    let mut i = self.lookup(key);
    if self
        .map
        .as_ref()
        .expect("map should have been created above")[i]
        .value
        == Default::default()
    {
        self.fill += 1;
        // resize when 2/3 full
        if self.fill * 3 >= (self.mask + 1) * 2 {
            self.grow((self.used + 1) * 2);
            i = self.lookup(key);
        }

        self.used += 1;
    }

    let elem = &mut self
        .map
        .as_mut()
        .expect("map should have been created above")[i];
    elem.key = key;
    &mut elem.value
}
